sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const {setUpdatedFieldsAndRefresh} = imports['sn_rf.RFHelper']();
    var params = {"dataBrokerId": "get_recommendations_1"};
    if (event.name == "SN_FORM_DATA_CONNECTED#FETCH_STATUS_UPDATED") {
        params["forceRefresh"] = true;
    }
    setUpdatedFieldsAndRefresh(api, event, helpers, params);
}
